'use strict';

var Command = require('ronin').Command;
var debug = require('debug');
var path = require('path');
var log = debug('cli:config');
log.error = debug('cli:config:error');
var utils = require('../../utils');
var fs = require('fs');

module.exports = Command.extend({
  desc: 'Replaces the config with <file>',

  options: {},

  run: function run(configPath) {
    utils.getIPFS(function (err, ipfs) {
      if (err) {
        throw err;
      }

      var filePath = path.resolve(process.cwd(), configPath);

      var config = utils.isDaemonOn() ? filePath : JSON.parse(fs.readFileSync(filePath, 'utf8'));

      ipfs.config.replace(config, function (err) {
        if (err) {
          throw err;
        }
      });
    });
  }
});