'use strict';

var Command = require('ronin').Command;
var debug = require('debug');
var utils = require('../../utils');
var log = debug('cli:files');
log.error = debug('cli:files:error');

module.exports = Command.extend({
  desc: 'Download IPFS objects',

  options: {},

  run: function run(path, options) {
    if (!path) {
      throw new Error("Argument 'path' is required");
    }
    if (!options) {
      options = {};
    }
    utils.getIPFS(function (err, ipfs) {
      if (err) {
        throw err;
      }
      if (utils.isDaemonOn()) {
        ipfs.cat(path, function (err, res) {
          if (err) {
            throw err;
          }
          console.log(res.toString());
        });
        return;
      }
      ipfs.files.cat(path, function (err, file) {
        if (err) {
          throw err;
        }
        file.pipe(process.stdout);
      });
    });
  }
});