'use strict';

var Command = require('ronin').Command;
var utils = require('../../utils');
var debug = require('debug');
var log = debug('cli:object');
log.error = debug('cli:object:error');

module.exports = Command.extend({
  desc: 'Outputs the links pointed to by the specified object',

  options: {},

  run: function run(key) {
    if (!key) {
      throw new Error("Argument 'key' is required");
    }

    utils.getIPFS(function (err, ipfs) {
      if (err) {
        throw err;
      }

      ipfs.object.links(key, { enc: 'base58' }, function (err, links) {
        if (err) {
          throw err;
        }

        links.forEach(function (link) {
          link = link.toJSON();
          console.log(link.Hash, link.Size, link.Name);
        });
      });
    });
  }
});