'use strict';

var Command = require('ronin').Command;
var utils = require('../../../utils');
var debug = require('debug');
var log = debug('cli:object');
var mDAG = require('ipfs-merkle-dag');
var DAGLink = mDAG.DAGLink;
log.error = debug('cli:object:error');

module.exports = Command.extend({
  desc: 'Add a link to a given object',

  options: {},

  run: function run(root, name, ref) {
    if (!root) {
      throw new Error("Argument 'root' is required");
    }
    if (!name) {
      throw new Error("Argument 'name' is required");
    }
    if (!ref) {
      throw new Error("Argument 'ref' is required");
    }

    utils.getIPFS(function (err, ipfs) {
      if (err) {
        throw err;
      }

      ipfs.object.get(ref, { enc: 'base58' }).then(function (linkedObj) {
        var link = new DAGLink(name, linkedObj.size(), linkedObj.multihash());
        return ipfs.object.patch.addLink(root, link, { enc: 'base58' });
      }).then(function (node) {
        console.log(node.toJSON().Hash);
      }).catch(function (err) {
        throw err;
      });
    });
  }
});