'use strict';

var Command = require('ronin').Command;
var utils = require('../../../utils');
var bl = require('bl');
var fs = require('fs');
var debug = require('debug');
var log = debug('cli:object');
log.error = debug('cli:object:error');

function appendData(key, data) {
  utils.getIPFS(function (err, ipfs) {
    if (err) {
      throw err;
    }

    ipfs.object.patch.appendData(key, data, { enc: 'base58' }, function (err, node) {
      if (err) {
        throw err;
      }

      console.log(node.toJSON().Hash);
    });
  });
}

module.exports = Command.extend({
  desc: 'Append data to the data segment of a dag node',

  options: {},

  run: function run(key, filePath) {
    if (!key) {
      throw new Error("Argument 'root' is required");
    }

    if (filePath) {
      return appendData(key, fs.readFileSync(filePath));
    }

    process.stdin.pipe(bl(function (err, input) {
      if (err) {
        throw err;
      }

      appendData(key, input);
    }));
  }
});