'use strict';

var Command = require('ronin').Command;
var DAGLink = require('ipfs-merkle-dag').DAGLink;
var utils = require('../../../utils');
var debug = require('debug');
var log = debug('cli:object');
log.error = debug('cli:object:error');

module.exports = Command.extend({
  desc: 'Remove a link from an object',

  options: {},

  run: function run(root, link) {
    if (!root) {
      throw new Error("Argument 'root' is required");
    }
    if (!link) {
      throw new Error("Argument 'link' is required");
    }

    utils.getIPFS(function (err, ipfs) {
      if (err) {
        throw err;
      }

      var dLink = new DAGLink(link);

      ipfs.object.patch.rmLink(root, dLink, { enc: 'base58' }, function (err, node) {
        if (err) {
          throw err;
        }

        console.log(node.toJSON().Hash);
      });
    });
  }
});