'use strict';

var Command = require('ronin').Command;
var utils = require('../../utils');
var bl = require('bl');
var fs = require('fs');
var debug = require('debug');
var log = debug('cli:object');
log.error = debug('cli:object:error');

function putNode(buf, enc) {
  utils.getIPFS(function (err, ipfs) {
    if (err) {
      throw err;
    }

    ipfs.object.put(buf, { enc: enc }, function (err, node) {
      if (err) {
        throw err;
      }

      console.log('added', node.toJSON().Hash);
    });
  });
}

module.exports = Command.extend({
  desc: 'Stores input as a DAG object, outputs its key',

  options: {
    inputenc: {
      type: 'string',
      default: 'json'
    }
  },

  run: function run(inputenc, filePath) {
    if (filePath) {
      return putNode(fs.readFileSync(filePath), inputenc);
    }

    process.stdin.pipe(bl(function (err, input) {
      if (err) {
        throw err;
      }

      putNode(input, inputenc);
    }));
  }
});