'use strict';

var Command = require('ronin').Command;
var utils = require('../../utils');
var debug = require('debug');
var log = debug('cli:swarm');
log.error = debug('cli:swarm:error');

module.exports = Command.extend({
  desc: 'Open connection to a given address',

  options: {},

  run: function run(address) {
    if (!address) {
      throw new Error("Argument 'address' is required");
    }

    utils.getIPFS(function (err, ipfs) {
      if (err) {
        throw err;
      }

      if (!utils.isDaemonOn()) {
        throw new Error('This command must be run in online mode. Try running \'ipfs daemon\' first.');
      }

      ipfs.swarm.connect(address, function (err, res) {
        if (err) {
          throw err;
        }

        console.log(res.Strings[0]);
      });
    });
  }
});