'use strict';

module.exports = function block(self) {
  return {
    get: function get(multihash, callback) {
      self._blockS.getBlock(multihash, callback);
    },
    put: function put(block, callback) {
      self._blockS.addBlock(block, callback);
    },
    del: function del(multihash, callback) {
      self._blockS.deleteBlock(multihash, callback);
    },
    stat: function stat(multihash, callback) {
      self._blockS.getBlock(multihash, function (err, block) {
        if (err) {
          return callback(err);
        }
        callback(null, {
          Key: multihash,
          Size: block.data.length
        });
      });
    }
  };
};