'use strict';

module.exports = function bootstrap(self) {
  return {
    list: function list(callback) {
      self._repo.config.get(function (err, config) {
        if (err) {
          return callback(err);
        }
        callback(null, config.Bootstrap);
      });
    },
    add: function add(multiaddr, callback) {
      self._repo.config.get(function (err, config) {
        if (err) {
          return callback(err);
        }
        config.Bootstrap.push(multiaddr);
        self._repo.config.set(config, callback);
      });
    },
    rm: function rm(multiaddr, callback) {
      self._repo.config.get(function (err, config) {
        if (err) {
          return callback(err);
        }

        config.Bootstrap = config.Bootstrap.filter(function (mh) {
          if (mh === multiaddr) {
            return false;
          } else {
            return true;
          }
        });
        self._repo.config.set(config, callback);
      });
    }
  };
};