'use strict';

var utils = require('../utils');

module.exports = function repo(self) {
  return {
    init: function init(bits, empty, callback) {
      // 1. check if repo already exists
    },

    version: function version(opts, callback) {
      if (typeof opts === 'function') {
        callback = opts;
        opts = {};
      }

      utils.ifRepoExists(self._repo, function (err, res) {
        if (err) {
          return callback(err);
        }

        self._repo.version.get(callback);
      });
    },

    gc: function gc() {},

    path: function path() {
      return self._repo.path;
    }
  };
};