'use strict';

var boom = require('boom');

exports = module.exports;

exports.list = function (request, reply) {
  request.server.app.ipfs.bootstrap.list(function (err, list) {
    if (err) {
      return reply(boom.badRequest(err));
    }
    return reply(list);
  });
};

