'use strict';

var boom = require('boom');

exports = module.exports;

exports.get = function (request, reply) {
  request.server.app.ipfs.version(function (err, ipfsVersion) {
    if (err) {
      return reply(boom.badRequest(err));
    }

    request.server.app.ipfs.repo.version(function (err, repoVersion) {
      if (err) {
        return reply(boom.badRequest(err));
      }

      reply({
        Version: ipfsVersion,
        Commit: '',
        Repo: repoVersion
      });
    });
  });
};