'use strict';

var resources = require('./../resources');

module.exports = function (server) {
  var api = server.select('API');

  api.route({
    method: '*',
    path: '/api/v0/cat',
    config: {
      pre: [{ method: resources.files.cat.parseArgs, assign: 'args' }],
      handler: resources.files.cat.handler
    }
  });
};